mapper.select:
SELECT dh.*
FROM devops_host dh
        WHERE project_id = #{projectId,jdbcType=BIGINT}
          AND host_ip IS NOT NULL
          AND id IN

    #{hostId,jdbcType=BIGINT}

SELECT * FROM ( SELECT dh.id, dh.name, dh.type, dh.project_id, dh.host_status, dh.host_check_error, dh.jmeter_status, dh.jmeter_check_error, dh.host_ip, dh.ssh_port, dh.auth_type, dh.username, dh.password, dh.jmeter_port, dh.jmeter_path, dh.object_version_number, dh.created_by, dh.creation_date, dh.last_updated_by, dh.last_update_date, 1 AS selected, CASE WHEN dh.host_status = 'failed' or dh.jmeter_status = 'failed' THEN 1 ELSE 0 END AS failed FROM devops_host dh
dh.type = 'distribute_test' and dh.id IN
#{id}
AND dh.name LIKE CONCAT(CONCAT('%', #{searchParam, jdbcType=VARCHAR}),'%')
union SELECT dh1.id, dh1.name, dh1.type, dh1.project_id, dh1.host_status, dh1.host_check_error, dh1.jmeter_status, dh1.jmeter_check_error, dh1.host_ip, dh1.ssh_port, dh1.auth_type, dh1.username, dh1.password, dh1.jmeter_port, dh1.jmeter_path, dh1.object_version_number, dh1.created_by, dh1.creation_date, dh1.last_updated_by, dh1.last_update_date, 0 AS selected, CASE WHEN dh1.host_status = 'failed' or dh1.jmeter_status = 'failed' THEN 1 ELSE 0 END AS failed FROM devops_host dh1
dh1.type = 'distribute_test' and dh1.project_id = #{projectId} and dh1.id not IN
#{id}
AND dh1.name LIKE CONCAT(CONCAT('%', #{searchParam, jdbcType=VARCHAR}),'%')
) h order by h.selected desc,h.failed desc

SELECT dh.*
FROM devops_host dh
WHERE dh.id IN

    #{hostId}

AND dh.project_id = #{projectId}
AND dh.type = 'distribute_test'

mapper.update:
UPDATE devops_host SET last_update_date = #{date}, host_status = 'operating', host_check_error = NULL,
last_updated_by = #{updatedBy}
, jmeter_status = 'operating', jmeter_check_error = NULL
WHERE project_id = #{projectId,jdbcType=BIGINT} AND id IN
#{hostId,jdbcType=BIGINT}

UPDATE devops_host
SET
jmeter_status = #{status}
WHERE id IN

    #{hostId,jdbcType=BIGINT}
