mapper.delete:
DELETE
FROM devops_issue_rel
        WHERE object = 'branch'
          AND object_id = #{branchId}
          AND issue_id IN

    #{issueId}

mapper.select:
SELECT issue_id
FROM devops_issue_rel dir
        WHERE dir.object = #{object}
          AND dir.object_id IN

    #{objectId}

SELECT dir.issue_id, dir.object_id
FROM devops_issue_rel dir
        WHERE dir.object = #{object}
          AND dir.object_id IN

    #{objectId}

SELECT dir.object_id
FROM devops_service.devops_issue_rel dir
WHERE dir.issue_id = #{issueId}
  AND dir.object = #{object}

SELECT issue_id, object_id
FROM devops_issue_rel dir
        WHERE dir.object = #{object}
          AND dir.issue_id IN

    #{issueId}
